import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";
import PropTypes from "prop-types";
import React from "react";
import { SchrijfInRequest } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieRequests";
import { Categorie } from "PropagandaSysteem/src/DomainLayer/Enums/Categorie";
import { SchrijfIn } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieUseCases";
import ApplicationState from "PropagandaSysteem/src/ApplicationLayer/ApplicationState";

export default function AddInschrijvingModal(props: any) {
  const [open, setOpen] = React.useState(false);
  const [schrijfInRequest, setSchrijfInRequest] = React.useState<
    SchrijfInRequest | undefined
  >({
    voornaam: "Nieuw",
    achternaam: "Inschrijving",
    club: "KCCM",
    categorie: Categorie.ASP_H,
    individueleWedstrijden: [
      {
        wedstrijd: "K1_1000",
        rondes: ["heat"],
      },
    ],
  });
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = () => {
    console.log(SchrijfIn(schrijfInRequest!, ApplicationState.getInstance()));
  };

  return (
    <div>
      <Button variant="outlined" onClick={handleClickOpen}>
        Add Inschrijving
      </Button>
      <Dialog open={open}>
        <DialogContent>
          <DialogContentText>
            Fill in the form to create a new inscription
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="firstName"
            label="First Name"
            type="text"
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="secondName"
            label="Second Name"
            type="text"
            fullWidth
            variant="standard"
          />

          <Select
            labelId="Categorie"
            id="category"
            label="Category"
            value={schrijfInRequest!.categorie}
            onChange={(e) => {
              schrijfInRequest!.categorie = e.target.value;
            }}
          >
            {Object.keys(Categorie).map((categorie: string) => {
              return (
                <MenuItem key={categorie} value={categorie}>
                  {categorie}
                </MenuItem>
              );
            })}
          </Select>

          <TextField
            autoFocus
            margin="dense"
            id="club"
            label="Club"
            type="text"
            fullWidth
            variant="standard"
          />

          <InputLabel id="demo-multiple-checkbox-label">Tag</InputLabel>
          <Select
            labelId="demo-multiple-checkbox-label"
            id="demo-multiple-checkbox"
            multiple
            value={personName}
            onChange={handleChange}
            input={<OutlinedInput label="Tag" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {names.map((name) => (
              <MenuItem key={name} value={name}>
                <Checkbox checked={personName.indexOf(name) > -1} />
                <ListItemText primary={name} />
              </MenuItem>
            ))}
          </Select>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSave}>Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

AddInschrijvingModal.propTypes = {
  onAddMenu: PropTypes.func,
};
