import { Button } from "@mui/material";
import React from "react";
import { Link } from "react-router-dom";

import ApplicationState from "PropagandaSysteem/src/ApplicationLayer/ApplicationState";
import FileReaderWeb from "../lib/FileReaderWeb";
import {
  LoadOrganisatieFromFile,
  LoadOrganisatieFromFileInWebbrowser,
} from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieUseCases";

async function showFile(e: any) {
  e.preventDefault();
  const reader = new FileReader();
  reader.onload = async (e) => {
    const text: string = e!.target!.result as string;
    console.log(text);
    alert(text);

    LoadOrganisatieFromFileInWebbrowser(
      {
        filecontent: text,
        overwrite: true,
      },
      ApplicationState.getInstance(),
      new FileReaderWeb()
    );
  };
  reader.readAsText(e!.target!.files![0]);
}

export default function WelcomeView() {
  return (
    <>
      <Link to="/mainview">
        <Button
          onClick={() => {
            console.log(
              LoadOrganisatieFromFile(
                {
                  path: "/Users/bramsikkens/Documents/Ontwikkeling/Propaganda/Savefiles/saved.txt",
                  overwrite: true,
                },
                ApplicationState.getInstance()
              )
            );
          }}
        >
          Click to go to MainView
        </Button>
      </Link>
      <input type="file" onChange={(e) => showFile(e)} />
    </>
  );
}
