    setRaceInfo(
      VerkrijgRaceInfo(ApplicationState.getInstance(), params.raceTitle!)
    );
  }, [params]);

  useEffect(() => {
    if (raceInfo) {
      setTeamCount(
        howManyTeamMembers(raceInfo?.raceInfo?.boottype as BootType)
      );
    }
  }, [raceInfo]);

