import { Button } from "@mui/material";
import React from "react";
import { Link } from "react-router-dom";

import ApplicationState from "PropagandaSysteem/src/ApplicationLayer/ApplicationState";
import { LoadOrganisatieFromFile } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieUseCases";

export default function WelcomeView() {
  showFile = async (e) => {
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      console.log(text);
      alert(text);
    };
    reader.readAsText(e.target.files[0]);
  };

  return (
    <>
      <Link to="/mainview">
        <Button
          onClick={() => {
            console.log(
              LoadOrganisatieFromFile(
                {
                  path: "/Users/bramsikkens/Documents/Ontwikkeling/Propaganda/Savefiles/saved.txt",
                  overwrite: true,
                },
                ApplicationState.getInstance()
              )
            );
          }}
        >
          Click to go to MainView
        </Button>

        <input type="file" onChange={(e) => this.showFile(e)} />
      </Link>
    </>
  );
}
