import {
  Button,
  Checkbox,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  InputLabel,
  ListItemText,
  MenuItem,
  OutlinedInput,
  Select,
  TextField,
} from "@mui/material";
import PropTypes from "prop-types";
import ApplicationState from "PropagandaSysteem/src/ApplicationLayer/ApplicationState";
import { SchrijfInRequest } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieRequests";
import { VerkrijgRaceInfoResponse } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieResponses";
import {
  SchrijfIn,
  VerkrijgRaceInfo,
} from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieUseCases";
import { verkrijgRondesVanRace } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/Race/RaceUseCases";
import { Categorie } from "PropagandaSysteem/src/DomainLayer/Enums/Categorie";
import React, { useEffect } from "react";
import { useParams } from "react-router";

export default function AddInschrijvingModal(props: any) {
  const params = useParams();
  const [open, setOpen] = React.useState(false);
  const [schrijfInRequest, setSchrijfInRequest] = React.useState<
    SchrijfInRequest | undefined
  >({
    voornaam: "Nieuw",
    achternaam: "Inschrijving",
    club: "KCCM",
    categorie: Categorie.SEN_H,
    individueleWedstrijden: [
      {
        wedstrijd: params.raceTitle!,
        rondes: ["semifinals"],
      },
    ],
  });

  const [raceInfo, setRaceInfo] = React.useState<
    VerkrijgRaceInfoResponse | undefined
  >(undefined);

  const [rondes, setRondes] = React.useState<Array<string> | undefined>(
    undefined
  );

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = () => {
    SchrijfIn(schrijfInRequest!, ApplicationState.getInstance());
    handleClose();
  };

  useEffect(() => {
    setRondes(
      verkrijgRondesVanRace(
        {
          raceTitle: params.raceTitle!,
        },
        ApplicationState.getInstance()
      ).rondes
    );

    setRaceInfo(
      VerkrijgRaceInfo(ApplicationState.getInstance(), params.raceTitle!)
    );
  }, [params]);

  const [rondeName, setRondeName] = React.useState<Array<string>>([]);

  const handleRondeChange = (event: any) => {
    const {
      target: { value },
    } = event;
    setRondeName(
      // On autofill we get a stringified value.
      typeof value === "string" ? value.split(",") : value
    );

    setSchrijfInRequest({
      ...(schrijfInRequest as SchrijfInRequest),
      individueleWedstrijden: [
        {
          wedstrijd: params.raceTitle!,
          rondes: typeof value === "string" ? value.split(",") : value,
        },
      ],
    });
  };

  return (
    <div>
      <Button variant="outlined" onClick={handleClickOpen}>
        Add Inschrijving
      </Button>
      <Dialog open={open}>
        <DialogContent>
          <DialogContentText>
            Fill in the form to create a new inscription
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="firstName"
            label="First Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={(e) => {
              setSchrijfInRequest({
                ...(schrijfInRequest as SchrijfInRequest),
                voornaam: e.target.value,
              });
            }}
          />
          <TextField
            autoFocus
            margin="dense"
            id="secondName"
            label="Second Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={(e) => {
              setSchrijfInRequest({
                ...(schrijfInRequest as SchrijfInRequest),
                achternaam: e.target.value,
              });
            }}
          />

          {raceInfo?.raceInfo?.boottype == "K2" ? (
            <>
              <TextField
                autoFocus
                margin="dense"
                id="firstName2"
                label="First Name 2"
                type="text"
                fullWidth
                variant="standard"
                onChange={(e) => {
                  setSchrijfInRequest({...Sc})

                }}
              />
              <TextField
                autoFocus
                margin="dense"
                id="secondName2"
                label="Second Name2"
                type="text"
                fullWidth
                variant="standard"
                onChange={(e) => {
                  setSchrijfInRequest({
                    ...(schrijfInRequest as SchrijfInRequest),
                    achternaam: e.target.value,
                  });
                }}
              />
            </>
          ) : undefined}

          <TextField
            autoFocus
            margin="dense"
            id="club"
            label="Club"
            type="text"
            fullWidth
            variant="standard"
            onChange={(e) => {
              setSchrijfInRequest({
                ...(schrijfInRequest as SchrijfInRequest),
                club: e.target.value,
              });
            }}
          />
          <InputLabel id="demo-multiple-checkbox-label">Category</InputLabel>

          <Select
            labelId="Categorie"
            id="category"
            label="Category"
            value={schrijfInRequest!.categorie}
            onChange={(e) => {
              setSchrijfInRequest({
                ...(schrijfInRequest as SchrijfInRequest),
                categorie: e.target.value,
              });
            }}
          >
            {Object.keys(Categorie).map((categorie: string) => {
              return (
                <MenuItem key={categorie} value={categorie}>
                  {categorie}
                </MenuItem>
              );
            })}
          </Select>

          <InputLabel id="demo-multiple-checkbox-label">Rounds</InputLabel>

          <Select
            labelId="demo-multiple-checkbox-label"
            id="demo-multiple-checkbox"
            multiple
            value={rondeName}
            onChange={handleRondeChange}
            input={<OutlinedInput label="Tag" />}
            renderValue={(selected) => selected.join(", ")}
            // MenuProps={MenuProps}
          >
            {rondes?.map((ronde: string) => (
              <MenuItem key={ronde} value={ronde}>
                <Checkbox checked={rondeName.indexOf(ronde) > -1} />
                <ListItemText primary={ronde} />
              </MenuItem>
            ))}
          </Select>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSave}>Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

AddInschrijvingModal.propTypes = {
  onAddMenu: PropTypes.func,
};
