import { TreeItem, TreeView } from "@material-ui/lab";
import { Box, Drawer } from "@mui/material";
import { makeStyles } from "@material-ui/core/styles";
import React, { useEffect, useState } from "react";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { Toolbar } from "@material-ui/core";
import { VerkrijgNavigationKeys } from "PropagandaSysteem/src/ApplicationLayer/Organisatie/OrganisatieUseCases";
import ApplicationState from "PropagandaSysteem/src/ApplicationLayer/ApplicationState";

const drawerWidth = 240;

const useStyles = makeStyles((theme: any) => ({
  root: {
    display: "flex",
    flexDirection: "column",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
}));

const generateMenu = (items: any) => {
  return (
    <>
      {items.map((item: any) => {
        if (item.children) {
          return (
            <TreeItem nodeId={item.index} key="1" label={item.title}>
              {generateMenu(item.children)}
            </TreeItem>
          );
        } else {
          return <TreeItem nodeId={item.index} key="1" label={item.title} />;
        }
      })}
    </>
  );
};

export default function NavigationDrawer() {
  const classes = useStyles();
  const [navigationItems, setNavigationItems] = useState([]);

  useEffect(() => {
    setNavigationItems(VerkrijgNavigationKeys(ApplicationState.getInstance()));
  }, []);

  return (
    <>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: "auto" }}>
          <TreeView
            className={classes.root}
            defaultCollapseIcon={<ExpandMoreIcon />}
            defaultExpandIcon={<ChevronRightIcon />}
          >
            {navigationItems.length > 0 && generateMenu(navigationItems)}
          </TreeView>
        </Box>
      </Drawer>
    </>
  );
}
